import React from 'react';
import { AlertTriangle, Shield, Info } from 'lucide-react';

const RiskSummary = ({ summary, simulation }) => {
  const getIndexIcon = (index) => {
    switch (index?.toLowerCase()) {
      case 'high risk':
        return <AlertTriangle className="w-5 h-5 text-red-500" />;
      case 'low risk':
        return <Shield className="w-5 h-5 text-green-500" />;
      default:
        return <Info className="w-5 h-5 text-yellow-500" />;
    }
  };

  const getIndexColor = (index) => {
    switch (index?.toLowerCase()) {
      case 'high risk':
        return 'bg-red-100 text-red-800 border-red-200';
      case 'low risk':
        return 'bg-green-100 text-green-800 border-green-200';
      default:
        return 'bg-yellow-100 text-yellow-800 border-yellow-200';
    }
  };

  if (!summary && !simulation) {
    return (
      <div className="h-full flex items-center justify-center text-gray-500">
        <p>No summary available</p>
      </div>
    );
  }

  return (
    <div className="h-full overflow-y-auto space-y-4">
      <h2 className="text-xl font-bold text-gray-900 sticky top-0 bg-white pb-2">
        Risk Summary
      </h2>

      {simulation && (
        <div className={`p-4 rounded-lg border ${getIndexColor(simulation.safety_index)}`}>
          <div className="flex items-center gap-2 mb-2">
            {getIndexIcon(simulation.safety_index)}
            <span className="font-semibold">Safety Index: {simulation.safety_index}</span>
          </div>
          <p className="text-sm">
            Total Clauses: {simulation.total_clauses}
          </p>
        </div>
      )}

      {summary && (
        <div className="bg-blue-50 p-4 rounded-lg border border-blue-200">
          <h3 className="font-semibold text-blue-900 mb-2">Plain Language Summary</h3>
          <div className="text-sm text-blue-800 whitespace-pre-wrap">
            {summary}
          </div>
        </div>
      )}

      {simulation?.simulation && (
        <div className="bg-purple-50 p-4 rounded-lg border border-purple-200">
          <h3 className="font-semibold text-purple-900 mb-2">Negotiation Simulation</h3>
          <div className="text-sm text-purple-800 whitespace-pre-wrap">
            {simulation.simulation}
          </div>
        </div>
      )}
    </div>
  );
};

export default RiskSummary;
