import React from 'react';

const ClauseHighlighter = ({ riskReport }) => {
  const getRiskColor = (riskLevel) => {
    switch (riskLevel?.toLowerCase()) {
      case 'high':
        return 'bg-red-100 border-red-300 text-red-800';
      case 'medium':
        return 'bg-yellow-100 border-yellow-300 text-yellow-800';
      case 'low':
        return 'bg-green-100 border-green-300 text-green-800';
      default:
        return 'bg-gray-100 border-gray-300 text-gray-800';
    }
  };

  const getRiskEmoji = (riskLevel) => {
    switch (riskLevel?.toLowerCase()) {
      case 'high':
        return 'ðŸ”´';
      case 'medium':
        return 'ðŸŸ ';
      case 'low':
        return 'ðŸŸ¢';
      default:
        return 'âšª';
    }
  };

  if (!riskReport || Object.keys(riskReport).length === 0) {
    return (
      <div className="h-full flex items-center justify-center text-gray-500">
        <p>Upload a contract to see clause analysis</p>
      </div>
    );
  }

  return (
    <div className="h-full overflow-y-auto space-y-4">
      <h2 className="text-xl font-bold text-gray-900 sticky top-0 bg-white pb-2">
        Contract Clauses
      </h2>
      {Object.entries(riskReport).map(([clauseId, details]) => {
        const riskLevel = details.analysis?.risk_level || 'Medium';
        const analysis = details.analysis?.analysis || 'No analysis available';

        return (
          <div
            key={clauseId}
            className={`p-4 rounded-lg border-2 ${getRiskColor(riskLevel)}`}
          >
            <div className="flex items-center justify-between mb-2">
              <h3 className="font-semibold flex items-center gap-2">
                {getRiskEmoji(riskLevel)} {clauseId}
              </h3>
              <span className="text-xs font-medium px-2 py-1 rounded">
                {riskLevel} Risk
              </span>
            </div>
            <p className="text-sm mb-3 text-gray-700">"{details.text}"</p>
            <div className="text-xs">
              <strong>Analysis:</strong> {analysis}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default ClauseHighlighter;
